$(function(){
	$("span#showmenu1").mouseover(function(event){
		x=event.pageX;
		y=event.pageY;
		x-=5;
		y-=5;	
		$("ul.v_menu").css({
			position:"absolute",
			left:x,
			top:y
		});
		$("ul.v_menu").show();
	});
	$("ul.v_menu").hover(function(){
 
	},function(){
		$("ul.v_menu").hide();
	});
	$("ul.v_menu").click(function(){
		$("ul.v_menu").hide();	
	});	
	$("span#showmenu2").mouseover(function(event){
		x=event.pageX;
		y=event.pageY;
		x-=5;
		y-=5;	
		$("ul.v_menu2").css({
			position:"absolute",
			left:x,
			top:y
		});
		$("ul.v_menu2").show();
	});
	$("ul.v_menu2").hover(function(){
 
	},function(){
		$("ul.v_menu2").hide();
	});
	$("ul.v_menu2").click(function(){
		$("ul.v_menu2").hide();	
	});	
	
	
	
});